mapper.insert:
insert into board(
	board_no, board_title, board_contents, board_regdate, board_hit, category_no, emp_no
)
values(
	#{board_no}, #{board_title}, #{board_contents}, SYSDATE, 0, #{category_no}, #{emp_no}
)

insert into board_file(
	fullname, board_no
)
values(
	#{fullName}, #{board_no}
)
