mapper.insert:
insert into schedule(
	schedule_no, schedule_regdate, schedule_title, schedule_contents, emp_no, schedule_start
)values(
	#{schedule_no}, SYSDATE, #{schedule_title}, #{schedule_contents}, #{emp_no}, #{schedule_start}
)

mapper.select:
select * from schedule s
	where emp_no = #{emp_no}
	and schedule_start = #{schedule_start}
