mapper.select:
SELECT COUNT(*) CNT 
	FROM NEWS_CLIPPING 
	WHERE NEWS_CLIPPING_LINK = #{news_clipping_link}
	AND EMP_NO = #{emp_no}
	AND NEWS_CLIPPING_DELDATE IS NULL

mapper.update:
UPDATE NEWS_CLIPPING
	SET NEWS_CLIPPING_DELDATE = SYSDATE
	WHERE EMP_NO = #{emp_no}
	AND NEWS_CLIPPING_LINK = #{news_clipping_link}
