mapper.update:
UPDATE elec_auth
SET elec_auth_deldate=SYSDATE
WHERE elec_auth_no=#{elec_auth_no}
AND emp_no=#{emp_no}

UPDATE approval_list
SET approval_list_pass='Y', approval_list_date=SYSDATE
WHERE elec_auth_no=#{elec_auth_no}
AND emp_no=#{emp_no}

UPDATE approval_list
SET approval_list_pass='N', approval_list_date=SYSDATE
WHERE elec_auth_no=#{elec_auth_no}
AND emp_no=#{emp_no}
